"""
Provider manager for coordinating between different AI model providers.
Handles provider selection, fallback logic, and unified interface.
"""

import logging
from typing import Any, Dict, List, Optional, Tuple

from .base import BaseModelProvider
from .bedrock import BedrockProvider
from .mistral import MistralProvider
from .custom import CustomProvider

logger = logging.getLogger(__name__)


class ProviderManager:
    """Manages multiple AI model providers with intelligent routing and fallback."""

    def __init__(self, config: Dict[str, Any]):
        """Initialize provider manager with configuration."""
        self.config = config
        self.ai_config = config.get("ai_models", {})
        self.providers: Dict[str, BaseModelProvider] = {}

        # Initialize providers based on configuration
        self._initialize_providers()

    def _initialize_providers(self):
        """Initialize available providers based on configuration."""
        provider_configs = self.ai_config.get("providers", {})

        # Initialize Bedrock provider (default enabled)
        bedrock_config = provider_configs.get("bedrock", {"enabled": True})
        if bedrock_config.get("enabled", True):
            try:
                self.providers["bedrock"] = BedrockProvider(bedrock_config)
                logger.info("Bedrock provider initialized")
            except Exception as e:
                logger.warning(f"Failed to initialize Bedrock provider: {e}")

        # Initialize Mistral provider (default disabled)
        mistral_config = provider_configs.get("mistral", {"enabled": False})
        if mistral_config.get("enabled", False):
            try:
                self.providers["mistral"] = MistralProvider(mistral_config)
                logger.info("Mistral provider initialized")
            except Exception as e:
                logger.warning(f"Failed to initialize Mistral provider: {e}")

        # Initialize Custom provider (default disabled)
        custom_config = provider_configs.get("custom", {"enabled": False})
        if custom_config.get("enabled", False):
            try:
                self.providers["custom"] = CustomProvider(custom_config)
                logger.info("Custom provider initialized")
            except Exception as e:
                logger.warning(f"Failed to initialize Custom provider: {e}")

    def generate_code(
        self, 
        provider: str, 
        description: str, 
        language: str = "python", 
        complexity: str = "medium",
        **kwargs
    ) -> Dict[str, Any]:
        """Generate code using a specific provider."""
        if provider not in self.providers:
            raise ValueError(f"Provider '{provider}' not available")

        return self.providers[provider].generate_code(
            description=description,
            language=language,
            complexity=complexity,
            **kwargs
        )

    def generate_code_with_fallback(
        self, 
        description: str, 
        language: str = "python", 
        complexity: str = "medium",
        **kwargs
    ) -> Dict[str, Any]:
        """Generate code with intelligent provider selection and fallback."""
        # Select best provider for the task
        task = {
            "language": language,
            "complexity": complexity,
            "description": description
        }

        providers_to_try = self._get_provider_fallback_sequence(task)

        last_error = None
        for provider_name in providers_to_try:
            if provider_name not in self.providers:
                continue

            provider = self.providers[provider_name]

            # Check if provider is available
            if not provider.is_available():
                logger.warning(f"Provider {provider_name} not available, trying next")
                continue

            try:
                logger.info(f"Attempting code generation with {provider_name}")
                result = provider.generate_code(
                    description=description,
                    language=language,
                    complexity=complexity,
                    **kwargs
                )

                # Check if generation was successful
                if "error" not in result and result.get("code"):
                    logger.info(f"Code generated successfully with {provider_name}")
                    result["provider_used"] = provider_name
                    return result
                else:
                    raise Exception(result.get("error", "Generation failed"))

            except Exception as e:
                logger.warning(f"Provider {provider_name} failed: {e}")
                last_error = e
                continue

        # All providers failed
        error_msg = f"All providers failed. Last error: {last_error}"
        logger.error(error_msg)
        return {
            "error": error_msg,
            "code": f"# All AI providers failed to generate {language} code",
            "model_used": "no-provider-available",
            "cost": 0.0,
            "language": language,
            "complexity": complexity
        }

    def select_provider_for_task(self, task: Dict[str, Any]) -> str:
        """Select the best provider for a given task."""
        language = task.get("language", "python")
        complexity = task.get("complexity", "medium")

        # Get strategy from config
        strategy = self.ai_config.get("fallback_strategy", "cost_optimized")

        # Infrastructure tasks prefer Mistral (if available)
        if self._is_infrastructure_task(language) and "mistral" in self.providers:
            mistral_config = self.ai_config.get("providers", {}).get("mistral", {})
            specialties = mistral_config.get("specialties", ["infrastructure", "yaml", "terraform"])

            if language in specialties and self.providers["mistral"].is_available():
                return "mistral"

        # High complexity tasks prefer Bedrock (quality first)
        if complexity == "high" and "bedrock" in self.providers:
            if self.providers["bedrock"].is_available():
                return "bedrock"

        # Cost-optimized strategy
        if strategy == "cost_optimized":
            # Try Custom first (potentially cheapest)
            if "custom" in self.providers and self.providers["custom"].is_available():
                return "custom"
            # Try Mistral second (cheaper than Bedrock)
            if "mistral" in self.providers and self.providers["mistral"].is_available():
                return "mistral"
            # Fall back to Bedrock
            if "bedrock" in self.providers and self.providers["bedrock"].is_available():
                return "bedrock"

        # Quality-first strategy
        elif strategy == "quality_first":
            # Try Bedrock first
            if "bedrock" in self.providers and self.providers["bedrock"].is_available():
                return "bedrock"
            # Fall back to Custom
            if "custom" in self.providers and self.providers["custom"].is_available():
                return "custom"
            # Fall back to Mistral
            if "mistral" in self.providers and self.providers["mistral"].is_available():
                return "mistral"

        # Default fallback order
        for provider in ["bedrock", "custom", "mistral"]:
            if provider in self.providers:
                return provider
        return "bedrock"  # Ultimate fallback

    def _get_provider_fallback_sequence(self, task: Dict[str, Any]) -> List[str]:
        """Get the sequence of providers to try for a task."""
        primary = self.select_provider_for_task(task)

        # Build fallback sequence
        sequence = [primary]

        # Add other available providers
        for provider_name in ["custom", "mistral", "bedrock"]:
            if provider_name != primary and provider_name in self.providers:
                sequence.append(provider_name)

        return sequence

    def _is_infrastructure_task(self, language: str) -> bool:
        """Check if this is an infrastructure-related task."""
        infrastructure_languages = [
            "terraform", "yaml", "yml", "kubernetes", "k8s", 
            "docker", "dockerfile", "ansible", "cloudformation"
        ]
        return language.lower() in infrastructure_languages

    def get_provider_status(self) -> Dict[str, Any]:
        """Get status of all providers."""
        status = {}

        for name, provider in self.providers.items():
            try:
                is_available = provider.is_available()
                status[name] = {
                    "available": is_available,
                    "type": type(provider).__name__,
                    "config": self.ai_config.get("providers", {}).get(name, {})
                }
            except Exception as e:
                status[name] = {
                    "available": False,
                    "error": str(e),
                    "type": type(provider).__name__
                }

        return {
            "providers": status,
            "default_strategy": self.ai_config.get("fallback_strategy", "cost_optimized"),
            "total_providers": len(self.providers),
            "available_providers": len([p for p in status.values() if p.get("available", False)])
        }

"""
Simple example showing FastMCP server with command line argument support.

Usage:
    fastmcp run examples/config_server.py -- --name MyServer --debug
"""

import argparse

from fastmcp import FastMCP

parser = argparse.ArgumentParser(description="Simple configurable MCP server")
parser.add_argument(
    "--name", type=str, default="ConfigurableServer", help="Server name"
)
parser.add_argument("--debug", action="store_true", help="Enable debug mode")

args = parser.parse_args()

server_name = args.name
if args.debug:
    server_name += " (Debug)"

mcp = FastMCP(server_name)


@mcp.tool
def get_status() -> dict[str, str | bool]:
    """Get the current server configuration and status."""
    return {
        "server_name": server_name,
        "debug_mode": args.debug,
        "original_name": args.name,
    }


@mcp.tool
def echo_message(message: str) -> str:
    """Echo a message, with debug info if debug mode is enabled."""
    if args.debug:
        return f"[DEBUG] Echoing: {message}"
    return message


if __name__ == "__main__":
    mcp.run()

"""
FastMCP Echo Server
"""

from fastmcp import FastMCP

# Create server
mcp = FastMCP("Echo Server")


@mcp.tool
def echo_tool(text: str) -> str:
    """Echo the input text"""
    return text


@mcp.resource("echo://static")
def echo_resource() -> str:
    return "Echo!"


@mcp.resource("echo://{text}")
def echo_template(text: str) -> str:
    """Echo the input text"""
    return f"Echo: {text}"


@mcp.prompt("echo")
def echo_prompt(text: str) -> str:
    return text

